-- municipios brasil
INSERT INTO municipio (id_estado, id_categoria, nome, codigo_ddd, rowinfo)
SELECT e.id, (SELECT c.id AS id_categoria FROM basico.tipo_categoria t
                    LEFT JOIN categoria c ON (t.id = c.id_tipo_categoria)
                WHERE t.nome = 'LOCALIDADE'
                AND c.nome = 'LOCALIDADE_ESTADO_MUNICIPIO') as id_categoria,
'Acrelândia' AS nome_cidade, NULL AS codigo_ddd, 'SYSTEM_STARTUP' AS rowinfo
FROM pais p
    LEFT JOIN estado e ON (e.id_pais = p.id)
WHERE p.constante_textual_nome = 'NOME_PAIS_BRASIL' AND e.nome = 'Acre';

